    (

select ParentId
        from Contents
        where Id = {0}
        union all
        select Contents.ParentId
        from Contents
            inner join CTE on Contents.Id = CTE.ParentId    
    )

    select *
    from CTE
    where ParentId is not null";
var parents = db.Database.SqlQuery<int>(string.Format(sql, selectedItem)).ToList();

